(49, 0, 8, '049_v_bretter_buchtipp_small.jpg', '049_v_bretter_buchtipp_big.jpg', '<p><span class="ueberschrift">BUCHTIPP</span></p>\r\n<p><span class="bildinfotitel">Vernetzungen</span> <br /> <span class="bildinfoquelle">von Gilbert Bretterbauer, Katalog</span></p>\r\n<p><span class="bildinfotitel">Der K&uuml;nstler als Seismograph. <br />Zur Gegenwart der Kunst und zur Kunst der Gegenwart</span> <br /> <span class="bildinfoquelle">von Uwe Fleckner</span></p>', '<p><span class="ueberschrift_g">BUCHTIPP</span></p>\r\n<p><span class="bildinfotitel_g">Vernetzungen</span>&nbsp;<br /><span class="bildinfoquelle_g">von Gilbert Bretterbauer, <br />Katalog, 72 Seiten, EUR 24,70; ISBN 978-3-85160-084-1</span></p>\r\n<p><span class="bildinfotitel_g">Der K&uuml;nstler als Seismograph. <br />Zur Gegenwart der Kunst und zur Kunst der Gegenwart</span>&nbsp;<br /><span class="bildinfoquelle_g">von Uwe Fleckner, 420 Seiten, EUR 18,&ndash;; ISBN-10: 3865726623</span></p>', '<p><span class="ueberschrift">RECOMMENDED BOOKS</span></p>\r\n<p><span class="bildinfotitel">Vernetzungen</span>&nbsp;<br /><span class="bildinfoquelle">by Gilbert Bretterbauer</span></p>\r\n<p><span class="bildinfotitel">Der K&uuml;nstler als Seismograph.</span><span class="bildinfoquelle"> <br /></span><span class="bildinfotitel">Zur Gegenwart der Kunst und zur Kunst der Gegenwart&nbsp;</span><br /><span class="bildinfoquelle">by&nbsp;Uwe Fleckner</span></p>', '<p><span class="ueberschrift_g">RECOMMENDED BOOKS</span></p>\r\n<p><span class="bildinfotitel_g">Vernetzungen&nbsp;<br /></span><span class="bildinfoquelle_g">by&nbsp;Gilbert Bretterbauer, <br />Katalog, 72 pages, EUR 24,70; ISBN 978-3-85160-084-1</span></p>\r\n<p><span class="bildinfotitel_g">Der K&uuml;nstler als Seismograph.</span><span class="bildinfoquelle_g"> <br /></span><span class="bildinfotitel_g">Zur Gegenwart der Kunst und zur Kunst der Gegenwart&nbsp;</span><br /><span class="bildinfoquelle_g">by&nbsp;Uwe Fleckner, 420 pages, EUR 18,&ndash;; ISBN-10: 3865726623</span></p>', '', '', '', '', 1, 1),
(50, 0, 8, '050_v_bretter_thumb04_small.jpg', '050_v_bretter_thumb04_big.jpg', '<p><span class="bildinfotitel">Das Wiener Musterzimmer<br /></span><span class="bildinfoquelle">ist ein Ausstellungsprojekt an der Schnittstelle von Kunst und Design, das von der Idee eines Idealraums, in dem alle raumbildenden Elemente, Materialien, Texturen, Strukturen und Farben aufeinander abgestimmt sind, ausgeht.</span></p>\r\n<p><span class="bildinfoquelle"><a href="http://www.youtube.com/watch?v=uc4038NjYYk">youtube.com</a></span></p>\r\n<p><span class="bildinfotitel">Bretterbauer Word-Performance</span><br /><span class="bildinfoquelle"><a href="http://www.tba21.org/augarten_activities/175">Ephemeropterae&tilde; X / XV+</a>,&nbsp;<br />17 August 2012</span></p>', '<p><span class="bildinfotitel_g">Das Wiener Musterzimmer<br /></span><span class="bildinfoquelle_g">ist ein Ausstellungsprojekt an der Schnittstelle von Kunst und Design, das von der Idee eines Idealraums, in dem alle raumbildenden Elemente, Materialien, Texturen, Strukturen und Farben aufeinander abgestimmt sind, ausgeht.</span></p>\r\n<p><span class="bildinfoquelle_g"><a href="http://www.youtube.com/watch?v=uc4038NjYYk">youtube.com</a></span></p>\r\n<p><span class="bildinfotitel_g">Bretterbauer Word-Performance</span><br /><span class="bildinfoquelle_g"><a href="http://www.tba21.org/augarten_activities/175">Ephemeropterae&tilde; X / XV+</a>,&nbsp;17 August 2012</span></p>', '<p><span class="bildinfotitel">Das Wiener Musterzimmer</span><br /><span class="bildinfoquelle">The Vienna Showroom is an exhibition project at the intersection of art and design, based on the idea of an ideal room in which all space-forming elements, materials, structures and colours are harmonised with one another.</span></p>\r\n<p><span class="bildinfoquelle"><a href="http://www.youtube.com/watch?v=uc4038NjYYk">youtube.com</a></span></p>\r\n<p><span class="bildinfotitel">Bretterbauer Word-Performance</span><br /><span class="bildinfotitel_g"><a href="http://www.tba21.org/augarten_activities/175">Ephemeropterae&tilde; X / XV+</a>,&nbsp;<br /></span><span class="bildinfoquelle">17 August 2012</span></p>', '<p><span class="bildinfotitel">Das Wiener Musterzimmer</span><br /><span class="bildinfoquelle_g">The Vienna Showroom is an exhibition project at the intersection of art and design, based on the idea of an ideal room in which all space-forming elements, materials, structures and colours are harmonised with one another.</span></p>\r\n<p><span class="bildinfoquelle_g"><a href="http://www.youtube.com/watch?v=uc4038NjYYk">youtube.com</a></span></p>\r\n<p><span class="bildinfotitel_g">Bretterbauer Word-Performance</span><br /><span class="bildinfoquelle_g"><a href="http://www.tba21.org/augarten_activities/175">Ephemeropterae&tilde; X / XV+</a>,&nbsp;17 August 2012</span></p>', '', '', '', '', 1, 1),
