(104, 6, 1, '104_nocover.gif', '104_nocover.gif', '<p><span class="ueberschrift">BUCHTIPPS</span></p>\r\n<p><span class="bildinfotitel"> &bdquo;Nachbrenner: Zur Evolution und Funktion des Spektakels</span>&ldquo;&nbsp;</p>\r\n<p><span class="bildinfoquelle">von Thomas Raab</span></p>\r\n<p><span class="bildinfotitel">&bdquo;Kritik der reinen Vernunft&ldquo;&nbsp;<br /></span><span class="bildinfoquelle">von Immanuel Kant</span></p>\r\n<p><span class="bildinfotitel">&bdquo;Die Mechanik in ihrer <br />Entwicklung&ldquo;&nbsp;<br /></span><span class="bildinfoquelle">von Ernst Mach (1883)</span></p>\r\n<p><span class="bildinfotitel">&bdquo;Atlas der Globalisierung&ldquo;&nbsp;</span><span class="bildinfoquelle">herausgegeben von Le monde diplomatique</span></p>\r\n<p><span class="bildinfotitel">&bdquo;&Uuml;berwachen und Strafen: <br />Die Geburt des Gef&auml;ngnisses&ldquo;&nbsp;<br /></span><span class="bildinfoquelle">von Michel Foucault</span></p>', '<p><span class="ueberschrift_g">BUCHTIPPS</span></p>\r\n<p><span class="bildinfotitel_g">&bdquo;Nachbrenner: Zur Evolution und Funktion des Spektakels</span>&ldquo;&nbsp;<br /><span class="bildinfoquelle_g">von Thomas Raab, 170 S., EUR 9,&ndash;, ISBN-10: 3518124587</span></p>\r\n<p><span class="bildinfotitel_g">&bdquo;Kritik der reinen Vernunft&ldquo; <br /></span><span class="bildinfoquelle_g">von Immanuel Kant (Kapitel "Die transzendentale &Auml;sthetik"), <br />568 S., EUR 29,95, ISBN-10: 3110014378</span></p>\r\n<p><span class="bildinfotitel_g">&bdquo;Die Mechanik in ihrer Entwicklung&ldquo;&nbsp;<br /></span><span class="bildinfoquelle_g">von Ernst Mach (1883), 582 S., EUR 38,&ndash;, ISBN-10: 3942106124</span></p>\r\n<p><span class="bildinfotitel_g">&bdquo;Atlas der Globalisierung&ldquo;<br /></span><span class="bildinfoquelle_g">herausgegeben von Le monde diplomatique, 176 S., EUR 14,&ndash;, <br />ISBN 978-3-937683-38-6</span></p>\r\n<p><span class="bildinfotitel_g">&bdquo;&Uuml;berwachen und Strafen: Die Geburt des Gef&auml;ngnisses&ldquo;&nbsp;<br /></span><span class="bildinfoquelle_g">von Michel Foucault, 408 Seiten, EUR 15,&ndash;; ISBN-10: 3518387715 <br />Marter, Bestrafung, Disziplin, Gef&auml;ngnis sind die vier gro&szlig;en Themen in Michel Foucaults Buch &uuml;ber die Entstehung des Gef&auml;ngnisses, mit dem er sich die Entwicklung der Gef&auml;ngnis- und Bestrafungsstrukturen seit Mitte des 19. Jahrhunderts vornimmt und die modernen Gesellschaften als "Disziplinargesellschaften" ausweist.&nbsp;</span></p>', '<p><span class="ueberschrift">RECOMMENDED BOOKS</span></p>\r\n<p><span class="bildinfotitel">"Nachbrenner: Zur Evolution und Funktion des Spektakels"</span><span class="bildinfoquelle"> <br />by Thomas Raab</span></p>\r\n<p><span class="bildinfotitel">"Kritik der reinen Vernunf"&nbsp;<br /></span><span class="bildinfoquelle">Immanuel Kant (Chapter &ldquo;Die transzendentale &Auml;sthetik&rdquo;)</span></p>\r\n<p><span class="bildinfotitel">"Die Mechanik in ihrer Entwicklung"</span><span class="bildinfoquelle_g"> <br /></span><span class="bildinfoquelle">Ernst Mach (1883)</span></p>\r\n<p><span class="bildinfotitel">"Atlas der Globalisierung"</span><span class="bildinfoquelle_g"> <br /></span><span class="bildinfoquelle">pub. Le Monde Diplomatique</span></p>', '<p><span class="ueberschrift_g">RECOMMENDED BOOKS</span></p>\r\n<p><span class="bildinfotitel_g">"Nachbrenner: Zur Evolution und Funktion des Spektakels" <br /></span><span class="bildinfoquelle_g">by</span><span class="bildinfoquelle_g">&nbsp;Thomas Raab, 170 S., EUR 9,&ndash;, ISBN-10: 3518124587</span></p>\r\n<p><span class="bildinfotitel_g">"Kritik der reinen Vernunf"&nbsp;<br /></span><span class="bildinfoquelle_g">Immanuel Kant (Chapter &ldquo;Die transzendentale &Auml;sthetik&rdquo;), <br />568 pp., &euro;29.95, ISBN-10: 3110014378</span></p>\r\n<p><span class="bildinfotitel_g">"Die Mechanik in ihrer Entwicklung"</span><span class="bildinfoquelle_g"> <br />Ernst Mach (1883), 582 pp., &euro;38; ISBN-10: 3942106124</span></p>\r\n<p><span class="bildinfotitel_g">"Atlas der Globalisierung"</span><span class="bildinfoquelle_g"> <br />pub. Le Monde Diplomatique, <br />176 pp., &euro;14;&nbsp;ISBN 978-3-937683-38-6</span></p>', '', '', '', '', 1, 1),
